mapper.select:
select count(*) from boards

	where writer like #{keyword}


	where subject like #{keyword}

select num, writer, email, subject, passwd, reg_date, readcount, ref, re_step, re_level, content, image 
from (select rownum as rank, num, writer, email, subject, passwd, reg_date, readcount, ref, re_step, re_level, content, image
from (select num, writer, email, subject, passwd, reg_date, readcount, ref, re_step, re_level, content, image 
from boards)

	where writer like #{keyword}


	where subject like #{keyword}

order by ref desc, re_step asc)
