mapper.select:
 		select count(*)
		from accounts,teachers where id=tid

 			and subject like #{keyword}


 			and tname like #{keyword}


 			and id like #{keyword}


 			and email like #{keyword}

select anum,tnum,aname,tname,id,tid,pw,rrn1,rrn2,email,phone1,phone2,phone3,addr,joindate,t_image,subject,visa,tterm,twageratio,introduction from accounts,teachers where id=tid
and subject like #{keyword}
and tname like #{keyword}
and id like #{keyword}
and email like #{keyword}
order by subject
order by tname
order by id
order by tterm
order by subject desc
order by tname desc
order by id desc
order by tterm desc
